import React, { useState, Component } from "react";
import "./App.css";
import axios from "axios";
import addQuestion from "./addQuestion";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      text: "",
      answer: [],
      lengthQuestion: 0,
    };
    this.handleText = this.handleText.bind(this);
    this.handleQuestion = this.handleQuestion.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleAnswer = this.handleAnswer.bind(this);
  }

  handleClick(e) {
    var data = {
      question: "test2",
    };
    const headers = {
      "Content-Type": "application/json",
    };

    e.preventDefault();
    axios
      .post("http://localhost:3000/questions", data, {
        headers: headers,
      })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  }

  handleQuestion(e) {
    e.preventDefault();

    axios
      .get("http://localhost:3000/questions/")
      .then((res) => {
        this.setState({ lengthQuestion: res.data.length });
      })
      .catch((err) => console.log(err));
    let pom = Math.floor(Math.random() * this.state.lengthQuestion) + 1;

    axios
      .get("http://localhost:3000/questions/" + pom)
      .then((res) => this.handleText(res))
      .catch((err) => console.log(err));

    axios
      .get("http://localhost:3000/answers/" + pom)
      .then((res) => this.handleAnswer(res))
      .catch((err) => console.log(err));
  }

  handleText(res) {
    let question = res.data.text;
    console.log(question);
    this.setState({ text: question });
    document.getElementById("text").innerHTML = question;
  }

  handleAnswer(res) {
    let answer = res.data;
    let lengthAnswer = answer.length;
    this.setState({ answer: answer });
    let pom = "";
    for (let index = 0; index < lengthAnswer; index++) {
      pom += answer[index].answer + "\n";
    }
    document.getElementById("answer").innerHTML = pom;
  }

  render() {
    return (
      <div className="App">
        <Router>
          <div>
            <nav>
              <ul>
                <li>
                  <Link to="/">Home</Link>
                </li>
                <li>
                  <Link to="/about">About</Link>
                </li>
                <li>
                  <Link to="/users">Users</Link>
                </li>
              </ul>
            </nav>

            {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
            <Switch>
              <Route path="/about">
                <addQuestion />
              </Route>
            </Switch>
          </div>
        </Router>
        <header className="App-header">
          <a href="addQuestion.js">Nowa strona</a>
          Pytanie
          <p id="text"></p>
          Odpowiedź
          <p id="answer"></p>
          <a href="#" onClick={this.handleQuestion}>
            Zmień pytanie
          </a>
        </header>
      </div>
    );
  }
}
